CREATE OR REPLACE FUNCTION sup_obter_preco_proc ( cd_procedimento_p bigint, ie_origem_proced_p bigint, cd_estabelecimento_p bigint) RETURNS bigint AS $body$
DECLARE


cd_tab_preco_proc_far_venda_w 		parametro_estoque.cd_tab_preco_proc_far_venda%type;
cd_tab_preco_proc_param_w	parametro_estoque.cd_tab_preco_proc_far_venda%type;
vl_preco_w 				double precision;


BEGIN

select	max(cd_tab_preco_proc_far_venda)
into STRICT	cd_tab_preco_proc_far_venda_w
from	parametro_estoque
where	cd_estabelecimento = cd_estabelecimento_p;

begin
	select obter_valor_param_usuario(1608, 3, obter_perfil_ativo, wheb_usuario_pck.get_nm_usuario, cd_estabelecimento_p)
	into STRICT   cd_tab_preco_proc_param_w
	;
exception
when others then
	cd_tab_preco_proc_param_w := null;
end;

if (cd_tab_preco_proc_param_w IS NOT NULL AND cd_tab_preco_proc_param_w::text <> '') then
	cd_tab_preco_proc_far_venda_w := cd_tab_preco_proc_param_w;
end if;

begin
select	vl_servico
into STRICT	vl_preco_w
from (
SELECT 	dt_inicio_vigencia, vl_servico
from 	preco_servico a
where 	a.cd_procedimento = cd_procedimento_p
and	a.ie_origem_proced = ie_origem_proced_p
and	a.cd_tabela_servico = cd_tab_preco_proc_far_venda_w
and	a.cd_estabelecimento = cd_estabelecimento_p
and	coalesce(a.dt_inativacao::text, '') = ''
and	a.dt_inicio_vigencia < clock_timestamp()
order by dt_inicio_vigencia desc) alias2 LIMIT 1;
exception
when others then
	vl_preco_w	:=	0;
end;

return coalesce(vl_preco_w,0);

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
