CREATE OR REPLACE FUNCTION sup_obter_proc_aprov_gerado ( nr_seq_aprovacao_p bigint, ie_retorno_p text) RETURNS varchar AS $body$
DECLARE


ds_retorno_w		varchar(255);
cd_processo_aprov_w	bigint;


BEGIN

if (nr_seq_aprovacao_p IS NOT NULL AND nr_seq_aprovacao_p::text <> '') then
	begin

	select	coalesce(max(cd_processo_aprov),0)
	into STRICT	cd_processo_aprov_w
	from	processo_compra
	where	nr_sequencia = nr_seq_aprovacao_p;

	if (ie_retorno_p = 'C') then
		ds_retorno_w	:= to_char(cd_processo_aprov_w);
	else
		begin

		select	ds_processo_aprov
		into STRICT	ds_retorno_w
		from	processo_aprovacao
		where	cd_processo_aprov = cd_processo_aprov_w;

		end;
	end if;

	end;
end if;

return	ds_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
