CREATE OR REPLACE FUNCTION sup_obter_qtde_lote_multiplo ( cd_material_p bigint, qt_material_p bigint) RETURNS bigint AS $body$
DECLARE


qt_lote_w		double precision;
qt_retorno_w		double precision;


BEGIN

select	coalesce(max(qt_compra_melhor),1)
into STRICT	qt_lote_w
from	material
where	cd_material = cd_material_p;

select	ceil(dividir(qt_material_p,qt_lote_w)) * qt_lote_w
into STRICT	qt_retorno_w
;

return	qt_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
