CREATE OR REPLACE FUNCTION sup_obter_qt_reservada_kit ( nr_seq_kit_estoque_p bigint, cd_material_p bigint, cd_estabelecimento_p bigint, ie_registro_kit_p bigint) RETURNS bigint AS $body$
DECLARE


qt_material_w			double precision := 0;

cd_material_estoque_w		bigint;
ie_disp_comp_kit_estoque_w	varchar(1);
ie_disp_reg_kit_estoque_w	varchar(1);


BEGIN

select	coalesce(max(ie_disp_comp_kit_estoque), 'N'),
	coalesce(max(ie_disp_reg_kit_estoque), 'N')
into STRICT	ie_disp_comp_kit_estoque_w,
	ie_disp_reg_kit_estoque_w
from	parametro_estoque
where	cd_estabelecimento	= cd_estabelecimento_p;

if 	(ie_registro_kit_p = 'N' AND ie_disp_comp_kit_estoque_w = 'S') or
	(ie_registro_kit_p = 'S' AND ie_disp_reg_kit_estoque_w = 'S') then

	select	cd_material_estoque
	into STRICT	cd_material_estoque_w
	from	material
	where	cd_material = cd_material_p;

	select	sum(a.qt_material)
	into STRICT	qt_material_w
	from	kit_estoque_comp a,
		material b
	where	a.nr_seq_kit_estoque = nr_seq_kit_estoque_p
	and	a.cd_material = b.cd_material
	and	b.cd_material_estoque = cd_material_estoque_w
	and	coalesce(a.ie_gerado_barras,'N') = 'N';

end if;

return	qt_material_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
