CREATE OR REPLACE FUNCTION sup_obter_regra_desdobra_req ( cd_material_p bigint, cd_local_estoque_p bigint, cd_local_destino_p bigint, cd_operacao_estoque_p bigint, cd_estabelecimento_p bigint) RETURNS bigint AS $body$
DECLARE


nr_seq_regra_w		regra_desdobramento_req.nr_sequencia%type;
qt_existe_w		bigint;
cd_grupo_material_w	smallint;
cd_subgrupo_material_w	smallint;
cd_classe_material_w	integer;
nr_seq_familia_w	bigint;
ie_desdobrar_w		regra_desdobramento_req.ie_desdobrar%type;	
ie_controlado_w		varchar(1);	
	

BEGIN

select	cd_grupo_material,
	cd_subgrupo_material,
	cd_classe_material,
	nr_seq_familia,
	substr(obter_se_medic_controlado(cd_material),1,1)
into STRICT	cd_grupo_material_w,
	cd_subgrupo_material_w,
	cd_classe_material_w,
	nr_seq_familia_w,
	ie_controlado_w
from	estrutura_material_v
where	cd_material = cd_material_p;

select	max(nr_sequencia),
	max(ie_desdobrar)
into STRICT	nr_seq_regra_w,
	ie_desdobrar_w
from(	SELECT	nr_sequencia,
		ie_desdobrar
	from	regra_desdobramento_req
	where	ie_situacao = 'A'
	and	cd_estabelecimento = cd_estabelecimento_p
	and	coalesce(cd_material,cd_material_p) = cd_material_p
	and	coalesce(cd_grupo_material,cd_grupo_material_w) = cd_grupo_material_w
	and	coalesce(cd_subgrupo_material,cd_subgrupo_material_w) = cd_subgrupo_material_w
	and	coalesce(cd_classe_material,cd_classe_material_w) = cd_classe_material_w
	and	coalesce(cd_operacao_estoque,cd_operacao_estoque_p) = cd_operacao_estoque_p
	and	coalesce(cd_local_estoque,cd_local_estoque_p) = cd_local_estoque_p
	and	coalesce(cd_local_estoque_desdob,cd_local_estoque_p) <> cd_local_estoque_p
	and	coalesce(nr_seq_familia,coalesce(nr_seq_familia_w,0)) = coalesce(nr_seq_familia_w,0)
	and	coalesce(cd_local_destino,coalesce(cd_local_destino_p,0)) = coalesce(cd_local_destino_p,0)
	and	((coalesce(ie_controlado,'A') = 'A') or (coalesce(ie_controlado, 'A') = ie_controlado_w))
	order by cd_material,
		nr_seq_familia,
		cd_classe_material,
		cd_subgrupo_material,
		cd_grupo_material,
		cd_local_estoque,
		cd_local_destino,
		ie_controlado desc,
		cd_operacao_estoque) LIMIT 1;

if (ie_desdobrar_w <> 'S') then
	nr_seq_regra_w := null;
end if;

return	nr_seq_regra_w;

end;
$body$
