CREATE OR REPLACE FUNCTION sup_obter_saldo_estoque ( cd_estabelecimento_p bigint, dt_mesano_referencia_p timestamp, cd_local_estoque_p bigint, cd_material_p bigint) RETURNS bigint AS $body$
DECLARE


qt_estoque_w		double precision;


/* Fabio 23/04/2010
É uma cópia da obter_saldo_estoque....
Esta é necessária nos casos em que não deve obter o material de estoque para buscar os saldos.
Acontece nos casos de obter saldos antigos e o cliente mudou o controlador
*/
BEGIN

select	coalesce(sum(qt_estoque), 0)
into STRICT	qt_estoque_w
from	saldo_estoque
where	cd_estabelecimento		= cd_estabelecimento_p
and	dt_mesano_referencia	= dt_mesano_referencia_p
and	cd_material		= cd_material_p
and	((cd_local_estoque = cd_local_estoque_p) or (coalesce(cd_local_estoque_p, 0) = 0));

return	qt_estoque_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
