CREATE OR REPLACE FUNCTION sup_obter_saldo_impressao_lote (nr_sequencia_p bigint) RETURNS bigint AS $body$
DECLARE


qt_material_w	bigint;
qt_impresso_w	bigint;


BEGIN

select	coalesce(sum(qt_material),0)
into STRICT	qt_material_w
from	material_lote_fornec
where	nr_sequencia = nr_sequencia_p;

select	coalesce(sum(qt_impressao),0)
into STRICT	qt_impresso_w
from	material_lote_fornec_etiq
where	nr_seq_lote_fornec = nr_sequencia_p
and	coalesce(dt_estorno_aprov::text, '') = '';

return	qt_material_w - qt_impresso_w;

end;
