CREATE OR REPLACE FUNCTION sup_obter_saldo_item_transf ( nr_ordem_compra_p bigint, nr_item_oci_p bigint) RETURNS bigint AS $body$
DECLARE


qt_saldo_w	double precision := 0;
qt_atendida_w	double precision;
qt_material_w	double precision;


BEGIN
if (nr_ordem_compra_p IS NOT NULL AND nr_ordem_compra_p::text <> '') and (nr_item_oci_p IS NOT NULL AND nr_item_oci_p::text <> '') then
	begin
	qt_atendida_w := coalesce(obter_qt_oci_trans_nota(nr_ordem_compra_p, nr_item_oci_p,'S'),0);

	select 	coalesce(sum(c.qt_prevista_entrega),0)
	into STRICT	qt_material_w
	from 	ordem_compra a,
		ordem_compra_item b,
		ordem_compra_item_entrega c
	where	a.nr_ordem_compra = b.nr_ordem_compra
	and	b.nr_item_oci = c.nr_item_oci
	and	b.nr_ordem_compra = c.nr_ordem_compra
	and	coalesce(c.dt_cancelamento::text, '') = ''
	and	a.nr_ordem_compra = nr_ordem_compra_p
	and	b.nr_item_oci	  = nr_item_oci_p;

	qt_saldo_w := qt_material_w - qt_atendida_w;
	end;
end if;

return	qt_saldo_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
