SET client_encoding TO 'UTF8';





CREATE OR REPLACE FUNCTION sup_obter_saldo_tit_pag_contr ( nr_titulo_p bigint, nr_contrato_p bigint) RETURNS bigint AS $body$
DECLARE


vl_saldo_titulo_w		titulo_pagar.vl_saldo_titulo%type;
vl_saldo_titulo_ww		titulo_pagar.vl_saldo_titulo%type := 0;
vl_saldo_adiant_w		titulo_pagar.vl_titulo%type;


BEGIN

select	vl_saldo_titulo
into STRICT	vl_saldo_titulo_w
from	titulo_pagar
where	nr_titulo = nr_titulo_p;

select	coalesce(sum(obter_saldo_contrato_adiant(nr_contrato_p)),0)
into STRICT	vl_saldo_adiant_w
;

vl_saldo_titulo_ww	:= vl_saldo_titulo_w - vl_saldo_adiant_w;

if (vl_saldo_titulo_ww < 0) then
	vl_saldo_titulo_ww := 0;
end if;

return	vl_saldo_titulo_ww;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
