CREATE OR REPLACE FUNCTION sup_obter_se_kit_consistido ( nr_seq_kit_estoque_p bigint) RETURNS varchar AS $body$
DECLARE


qt_componentes_w	bigint;
qt_nao_consistidos_w	bigint;
ds_retorno_w		varchar(1) := 'N';


BEGIN

select  count(*)
into STRICT	qt_nao_consistidos_w
from    kit_estoque_comp a
where   a.nr_seq_kit_estoque = nr_seq_kit_estoque_p
and     a.ie_gerado_barras not in ('S','E');

select  count(*)
into STRICT	qt_componentes_w
from    kit_estoque_comp a
where   a.nr_seq_kit_estoque = nr_seq_kit_estoque_p
and	a.ie_gerado_barras not in ('E');

if (qt_componentes_w > 0) and (qt_nao_consistidos_w = 0) then
	ds_retorno_w := 'S';
end if;

return	ds_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
