CREATE OR REPLACE FUNCTION sup_obter_se_permite_parecer ( nr_seq_parecer_p bigint, nm_usuario_p text) RETURNS varchar AS $body$
DECLARE


ie_retorno_w		varchar(1)	:= 'N';
cd_pessoa_fisica_w	varchar(10);
cd_setor_atendimento_w	integer;
cd_pessoa_fisica_usu_w	varchar(10);
qt_existe_w		bigint;


BEGIN

select	coalesce(cd_pessoa_fisica,'X'),
	coalesce(cd_setor_atendimento,0)
into STRICT	cd_pessoa_fisica_w,
	cd_setor_atendimento_w
from	segmento_compras_rel_parec
where	nr_sequencia = nr_seq_parecer_p;

select	cd_pessoa_fisica
into STRICT	cd_pessoa_fisica_usu_w
from	usuario
where	nm_usuario = nm_usuario_p;

if (cd_pessoa_fisica_w <> 'X') and (cd_pessoa_fisica_w = cd_pessoa_fisica_usu_w) then

	ie_retorno_w := 'S';

elsif (cd_pessoa_fisica_w = 'X') then
	begin

	select	count(*)
	into STRICT	qt_existe_w
	from	usuario_setor_v
	where	nm_usuario = nm_usuario_p
	and	cd_setor_atendimento = cd_setor_atendimento_w;

	if (qt_existe_w > 0) then
		ie_retorno_w	:= 'S';
	end if;

	end;
end if;

return	ie_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
