CREATE OR REPLACE FUNCTION sup_obter_se_usuario_lider ( nm_usuario_p text) RETURNS varchar AS $body$
DECLARE


ds_retorno_w		varchar(1) := 'S';


BEGIN

select	CASE WHEN count(1)=0 THEN 'N'  ELSE 'S' END
into STRICT	ds_retorno_w
from	gerencia_wheb a,
	grupo_suporte b
where	a.nr_sequencia = b.nr_seq_gerencia_sup
and	a.ie_area_gerencia = 'SUP'
and	b.nm_usuario_lider = nm_usuario_p;

return	ds_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
