SET client_encoding TO 'UTF8';





CREATE OR REPLACE FUNCTION sup_obter_status_etiquetagem ( nr_sequencia_p bigint, ie_opcao_p text) RETURNS varchar AS $body$
DECLARE


/* ie_opcao_p
D - Descrição
C - Código
*/
ie_status_w		varchar(1);
ds_status_w		varchar(255);
ds_retorno_w		varchar(255);
dt_aprovacao_w		timestamp;
dt_separacao_w		timestamp;
dt_fim_preparo_w	timestamp;
dt_conferencia_w	timestamp;
dt_cancelamento_w	timestamp;


BEGIN

select	dt_aprovacao,
	dt_separacao,
	dt_fim_preparo,
	dt_conferencia,
	dt_cancelamento
into STRICT	dt_aprovacao_w,
	dt_separacao_w,
	dt_fim_preparo_w,
	dt_conferencia_w,
	dt_cancelamento_w
from	material_lote_fornec_etiq
where	nr_sequencia = nr_sequencia_p;

if (dt_cancelamento_w IS NOT NULL AND dt_cancelamento_w::text <> '') then
	ie_status_w := 'L';
elsif (dt_conferencia_w IS NOT NULL AND dt_conferencia_w::text <> '') then
	ie_status_w := 'C';
elsif (dt_fim_preparo_w IS NOT NULL AND dt_fim_preparo_w::text <> '') then
	ie_status_w := 'P';
elsif (dt_separacao_w IS NOT NULL AND dt_separacao_w::text <> '') then
	ie_status_w := 'S';
elsif (dt_aprovacao_w IS NOT NULL AND dt_aprovacao_w::text <> '') then
	ie_status_w := 'A';
end if;

ds_status_w := substr(obter_valor_dominio(5332, ie_status_w),1,255);

if (ie_opcao_p = 'D') then
	ds_retorno_w := ds_status_w;
else
	ds_retorno_w := ie_status_w;
end if;

return	ds_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
