SET client_encoding TO 'UTF8';





CREATE OR REPLACE FUNCTION sup_obter_status_item_req (nr_requisicao_p bigint, nr_sequencia_p bigint) RETURNS varchar AS $body$
DECLARE


cd_motivo_baixa_w	bigint;
cd_tipo_baixa_w		bigint;
ds_retorno_w		varchar(10);


BEGIN
select	coalesce(max(cd_motivo_baixa),0)
into STRICT	cd_motivo_baixa_w
from	item_requisicao_material
where	nr_sequencia = nr_sequencia_p
and	nr_requisicao = nr_requisicao_p;

if (cd_motivo_baixa_w > 0) then
	select	cd_motivo_baixa
	into STRICT	cd_tipo_baixa_w
	from	sup_motivo_baixa_req
	where	nr_sequencia = cd_motivo_baixa_w;
else
	cd_tipo_baixa_w := 0;
end if;

if (cd_tipo_baixa_w = 0) then
	ds_retorno_w := 'P';
elsif (cd_tipo_baixa_w in (1,4,5)) then
	ds_retorno_w := 'A';
elsif (cd_tipo_baixa_w in (2,3,6)) then
	ds_retorno_w := 'NA';
end if;

return	ds_retorno_w;
end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
