CREATE OR REPLACE FUNCTION sup_obter_total_aprov_mes ( cd_material_p bigint, dt_mesano_referencia_p timestamp, cd_estabelecimento_p bigint, ie_tipo_documento_p text, ie_agrupamento_p text) RETURNS bigint AS $body$
DECLARE

 
/* 
Agrupamento 
G - Grupo de Material 
*/
 
 
vl_retorno_w			double precision	:= 0;
vl_ordem_compra_w		double precision	:= 0;
cd_grupo_material_w		smallint;
cd_subgrupo_material_w		bigint;
cd_classe_material_w		bigint;


BEGIN 
 
select	cd_grupo_material, 
	cd_subgrupo_material, 
	cd_classe_material 
into STRICT	cd_grupo_material_w, 
	cd_subgrupo_material_w, 
	cd_classe_material_w 
from	estrutura_material_v 
where	cd_material = cd_material_p;
 
if (ie_tipo_documento_p = 'O') or (ie_tipo_documento_p = 'X') then 
	begin 
 
	select	coalesce(sum(b.vl_item_liquido),0) 
	into STRICT	vl_ordem_compra_w 
	from	ordem_compra a, 
		ordem_compra_item b, 
		estrutura_material_v e 
	where	a.nr_ordem_compra = b.nr_ordem_compra 
	and	e.cd_material = b.cd_material 
	and	coalesce(e.cd_grupo_material, cd_grupo_material_w)		= cd_grupo_material_w 
	and	coalesce(e.cd_subgrupo_material, cd_subgrupo_material_w)	= cd_subgrupo_material_w 
	and	coalesce(e.cd_classe_material, cd_classe_material_w)		= cd_classe_material_w 
	and	a.cd_estabelecimento = cd_estabelecimento_p 
	and	(a.dt_liberacao IS NOT NULL AND a.dt_liberacao::text <> '') 
	and	trunc(a.dt_liberacao,'mm') = dt_mesano_referencia_p 
	and	(b.nr_seq_aprovacao IS NOT NULL AND b.nr_seq_aprovacao::text <> '') 
	and	(b.dt_aprovacao IS NOT NULL AND b.dt_aprovacao::text <> '') 
	and	coalesce(b.dt_reprovacao::text, '') = '';
 
	end;
end if;
 
vl_retorno_w	:= vl_ordem_compra_w;
 
return	vl_retorno_w;
 
end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
