CREATE OR REPLACE FUNCTION sup_obter_valores_oci ( nr_ordem_compra_p bigint, nr_item_oci_p bigint, ie_opcao_p text) RETURNS bigint AS $body$
DECLARE


/* ie_opcao_p
VD	Valor do desconto do item
PD	% de desconto do item
QT	Quantidade do material
VU	Valor unitario do item
VB	Valor bruto (quantidade * valor unitario)
VL	Valor liquido do item
TD	Total de descontos do item
*/
vl_retorno_w			double precision := 0;
vl_desconto_w			double precision;
pr_descontos_w			double precision;
vl_item_liquido_w			double precision;
vl_unitario_material_w		double precision;
qt_material_w			double precision;

BEGIN

select	coalesce(vl_desconto,0),
	coalesce(pr_descontos,0),
	vl_item_liquido,
	vl_unitario_material,
	qt_material
into STRICT	vl_desconto_w,
	pr_descontos_w,
	vl_item_liquido_w,
	vl_unitario_material_w,
	qt_material_w
from	ordem_compra_item
where	nr_ordem_compra = nr_ordem_compra_p
and	nr_item_oci = nr_item_oci_p;

if (ie_opcao_p = 'VD') then
	vl_retorno_w := vl_desconto_w;
elsif (ie_opcao_p = 'PD') then
	vl_retorno_w := pr_descontos_w;
elsif (ie_opcao_p = 'QT') then
	vl_retorno_w := qt_material_w;
elsif (ie_opcao_p = 'VU') then
	vl_retorno_w := vl_unitario_material_w;
elsif (ie_opcao_p = 'VB') then
	vl_retorno_w := (vl_unitario_material_w * qt_material_w);
elsif (ie_opcao_p = 'VL') then
	vl_retorno_w := vl_item_liquido_w;
elsif (ie_opcao_p = 'TD') then
	vl_retorno_w := round((vl_unitario_material_w * qt_material_w)::numeric,2) - vl_item_liquido_w;
end if;

return	vl_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
