CREATE OR REPLACE FUNCTION sup_obter_valor_aprovacao ( nr_seq_aprovacao_p bigint, cd_centro_custo_p bigint, cd_conta_contabil_p text, ie_tipo_p text) RETURNS bigint AS $body$
DECLARE

 
vl_retorno_w				bigint;


BEGIN 
 
if (ie_tipo_p = 'O') then 
 
	select	coalesce(sum(b.qt_material * b.vl_unitario_material),0) vl_total 
	into STRICT	vl_retorno_w 
	from	ordem_compra a, 
		ordem_compra_item b 
	where	a.nr_ordem_compra = b.nr_ordem_compra 
	and	coalesce(b.dt_aprovacao::text, '') = '' 
	and	coalesce(b.dt_reprovacao::text, '') = '' 
	and	nr_seq_aprovacao = nr_seq_aprovacao_p;
	 
elsif (ie_tipo_p = 'S') then 
 
	select	coalesce(sum(b.qt_material * coalesce(b.vl_unit_previsto,0)),0)vl_total 
	into STRICT	vl_retorno_w 
	from	solic_compra a, 
		solic_compra_item b 
	where	a.nr_solic_compra = b.nr_solic_compra 
	and	coalesce(b.dt_reprovacao::text, '') = '' 
	and	coalesce(b.dt_autorizacao::text, '') = '' 
	and	nr_seq_aprovacao = nr_seq_aprovacao_p 
	and	a.cd_centro_custo	= cd_centro_custo_p 
	and	b.cd_conta_contabil	= cd_conta_contabil_p;
	 
end if;
 
return	vl_retorno_w;
 
end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
