CREATE OR REPLACE FUNCTION sup_obter_valor_estoque ( cd_estabelecimento_p bigint, dt_mesano_referencia_p timestamp, cd_local_estoque_p bigint, cd_material_p bigint) RETURNS bigint AS $body$
DECLARE


vl_estoque_w		double precision;


BEGIN

select	coalesce(sum(vl_estoque), 0)
into STRICT	vl_estoque_w
from	saldo_estoque
where	cd_estabelecimento		= cd_estabelecimento_p
and	dt_mesano_referencia	= dt_mesano_referencia_p
and	cd_material		= cd_material_p
and	((cd_local_estoque 		= cd_local_estoque_p) or (coalesce(cd_local_estoque_p, 0) = 0));

return	vl_estoque_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
