CREATE OR REPLACE FUNCTION sus_apresenta_protocolo ( nr_seq_protocolo_p bigint, ie_tipo_financiamento_p text, ie_complexidade_p text) RETURNS varchar AS $body$
DECLARE

					 
ds_retorno_w		varchar(15);
ie_tipo_financ_w		varchar(15);
ie_complexidade_w		varchar(15);


BEGIN 
 
select	coalesce(max(ie_tipo_financ_sus),'0'), 
	coalesce(max(ie_complexidade_sus),'0') 
into STRICT	ie_tipo_financ_w, 
	ie_complexidade_w 
from	protocolo_convenio 
where	nr_seq_protocolo = nr_seq_protocolo_p;
 
if (ie_tipo_financ_w <> '0') and (ie_tipo_financ_w = ie_tipo_financiamento_p) and (ie_complexidade_w <> '0') and (ie_complexidade_w = ie_complexidade_p) then 
	ds_retorno_w := 'S';
elsif (ie_tipo_financ_w <> '0') and (ie_tipo_financ_w = ie_tipo_financiamento_p) and (ie_complexidade_w = '0') then 
	ds_retorno_w := 'S';
elsif (ie_tipo_financ_w = '0') and (ie_complexidade_w <> '0') and (ie_complexidade_w = ie_complexidade_p) then 
	ds_retorno_w := 'S';
else 
	ds_retorno_w := 'N';
end if;
 
return	ds_retorno_w;
 
end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
