CREATE OR REPLACE FUNCTION sus_consiste_cod_autor (cd_procedimento_p bigint, ie_origem_proced_p bigint, ie_codigo_autorizacao_p bigint) RETURNS varchar AS $body$
DECLARE


cd_detalhe_w			varchar(5);
ds_codigo_autor_w		varchar(255);
ie_retorno_w			varchar(15) := 'N';

C01 CURSOR FOR
	SELECT	a.cd_detalhe
	from	sus_detalhe a,
		sus_detalhe_proc b
	where	b.nr_seq_detalhe = a.nr_sequencia
	and	b.cd_procedimento = cd_procedimento_p
	and	b.ie_origem_proced = ie_origem_proced_p;


BEGIN

select	coalesce(obter_valor_dominio(1940,ie_codigo_autorizacao_p),'X')
into STRICT	ds_codigo_autor_w
;

open C01;
loop
fetch C01 into
	cd_detalhe_w;
EXIT WHEN NOT FOUND; /* apply on C01 */
	begin

	if (cd_detalhe_w = '10021') and (obter_se_contido_char('CBO',ds_codigo_autor_w) = 'S')	then
		ie_retorno_w := 'S';
	elsif (cd_detalhe_w = '10021') and (obter_se_contido_char('CNS',ds_codigo_autor_w) = 'S')	then
		ie_retorno_w := 'S';
	elsif (cd_detalhe_w = '009') and (obter_se_contido_char('CNS',ds_codigo_autor_w) = 'S')	then
		ie_retorno_w := 'S';
	elsif (cd_detalhe_w = '10049') and
		((obter_se_contido_char('Qtd',ds_codigo_autor_w) = 'S') or (obter_se_contido_char('Quantidade',ds_codigo_autor_w) = 'S'))	then
		ie_retorno_w := 'S';
	end if;

	end;
end loop;
close C01;

return	ie_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
