CREATE OR REPLACE FUNCTION sus_consiste_data_laudo ( cd_pessoa_fisica_p text) RETURNS varchar AS $body$
DECLARE

 
qt_reg_laudo_w	integer	:= 0;
ds_retorno_w	varchar(1)	:= 'N';


BEGIN 
 
select	count(*) 
into STRICT	qt_reg_laudo_w 
from	atendimento_paciente		b, 
	sus_laudo_paciente		a 
where	a.nr_atendimento		= b.nr_atendimento 
and	b.cd_pessoa_fisica		= cd_pessoa_fisica_p 
and	a.ie_tipo_laudo_apac		= 1 
and	to_char(dt_emissao,'mm/yyyy')	= to_char(clock_timestamp(),'mm/yyyy');
 
if (qt_reg_laudo_w	> 0) then 
	ds_retorno_w	:= 'S';	
end if;
 
return	ds_retorno_w;
 
END;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
