CREATE OR REPLACE FUNCTION sus_consiste_habilitacao (cd_procedimento_p bigint, ie_origem_proced_p bigint, nr_dias_p bigint, cd_estabelecimento_p bigint) RETURNS varchar AS $body$
DECLARE


ds_retorno_w		varchar(1) := 'N';
cd_habilitacao_w	smallint;
dt_final_vigencia_w	timestamp;


BEGIN

select	max(cd_habilitacao)
into STRICT	cd_habilitacao_w
from	sus_proced_habilitacao
where	cd_procedimento		= cd_procedimento_p
and	ie_origem_proced	= ie_origem_proced_p;

begin
select	max(dt_final_vigencia)
into STRICT	dt_final_vigencia_w
from	sus_habilitacao_hospital
where	cd_habilitacao		= cd_habilitacao_w
and	cd_estabelecimento	= cd_estabelecimento_p;
exception
	when others then
	dt_final_vigencia_w	:= null;
end;

if (dt_final_vigencia_w IS NOT NULL AND dt_final_vigencia_w::text <> '') and
	((dt_final_vigencia_w - clock_timestamp()) <= nr_dias_p) then
	begin
	ds_retorno_w	:= 'S';
	end;
end if;

return	ds_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
