SET client_encoding TO 'UTF8';





CREATE OR REPLACE FUNCTION sus_consiste_limite_apac ( dt_competencia_p timestamp, cd_procedimento_p bigint, cd_estabelecimento_p bigint, nr_interno_conta_p bigint, ie_opcao_p text) RETURNS varchar AS $body$
DECLARE


qt_apac_competencia_w		bigint;
vl_apac_competencia_w		double precision;
vl_apac_atual_w			double precision;
qt_apac_regra_w			bigint;
vl_apac_regra_w			double precision;
qt_registro_w			varchar(10);
ds_retorno_w			varchar(255) := '';
ie_complexidade_w			varchar(2);
ie_tipo_financiamento_w		varchar(4);
nr_seq_regra_w			bigint;
nr_seq_grupo_w			bigint;
nr_seq_subgrupo_w			bigint;
nr_seq_forma_org_w		bigint;
cd_municipio_ibge_w		varchar(6);

/*IE_OPCAO_P:
E - Emissão
P - Protocolo
*/
BEGIN

begin
select	count(*)
into STRICT	qt_registro_w
from	sus_limite_apac_comp
where	trunc(dt_competencia,'month')	= trunc(dt_competencia_p,'month')
and	cd_estabelecimento 		= cd_estabelecimento_p;
exception
	when others then
	qt_registro_w := 0;
end;

if ( qt_registro_w > 0) then
	begin

	begin
	select	sus_obter_municipio_atend(nr_atendimento,'C')
	into STRICT	cd_municipio_ibge_w
	from	conta_paciente
	where	nr_interno_conta = nr_interno_conta_p;
	exception
		when others then
		cd_municipio_ibge_w := 'X';
	end;

	begin
	select	a.ie_tipo_financiamento,
		a.ie_complexidade,
		b.nr_seq_grupo,
		b.nr_seq_subgrupo,
		b.nr_seq_forma_org
	into STRICT	ie_tipo_financiamento_w,
		ie_complexidade_w,
		nr_seq_grupo_w,
		nr_seq_subgrupo_w,
		nr_seq_forma_org_w
	from	sus_procedimento a,
		sus_estrutura_procedimento_v b
	where	a.cd_procedimento	= cd_procedimento_p
	and	a.ie_origem_proced	= 7
	and	a.cd_procedimento	= b.cd_procedimento
	and	a.ie_origem_proced	= b.ie_origem_proced;
	exception
		when others then
		ie_tipo_financiamento_w 	:= 'X';
		ie_complexidade_w		:= 'X';
		nr_seq_grupo_w		:= 0;
		nr_seq_subgrupo_w		:= 0;
		nr_seq_forma_org_w	:= 0;
	end;


	select	sus_obter_limite_apac(dt_competencia_p,ie_complexidade_w,ie_tipo_financiamento_w,1,cd_estabelecimento_p,nr_seq_grupo_w,nr_seq_subgrupo_w,nr_seq_forma_org_w,cd_municipio_ibge_w)
	into STRICT	qt_apac_regra_w
	;

	select	sus_obter_limite_apac(dt_competencia_p,ie_complexidade_w,ie_tipo_financiamento_w,2,cd_estabelecimento_p,nr_seq_grupo_w,nr_seq_subgrupo_w,nr_seq_forma_org_w,cd_municipio_ibge_w)
	into STRICT	vl_apac_regra_w
	;

	select	sus_obter_limite_apac(dt_competencia_p,ie_complexidade_w,ie_tipo_financiamento_w,3,cd_estabelecimento_p,nr_seq_grupo_w,nr_seq_subgrupo_w,nr_seq_forma_org_w,cd_municipio_ibge_w)
	into STRICT	nr_seq_regra_w
	;


	if (ie_opcao_p = 'E') then
		begin

		select	count(*)
		into STRICT	qt_apac_competencia_w
		from	sus_apac_unif a,
			sus_limite_apac_comp b
		where	trunc(a.dt_emissao,'month') 				= trunc(b.dt_competencia,'month')
		and	coalesce(b.ie_tipo_financiamento,ie_tipo_financiamento_w) 	= ie_tipo_financiamento_w
		and	coalesce(b.ie_complexidade,ie_complexidade_w)		= ie_complexidade_w
		and	((coalesce(b.nr_seq_grupo,0) = 0) or (b.nr_seq_grupo = sus_obter_estrut_proc(a.cd_procedimento,a.ie_origem_proced,'S','G')))
		and	((coalesce(b.nr_seq_subgrupo,0) = 0) or (b.nr_seq_subgrupo = sus_obter_estrut_proc(a.cd_procedimento,a.ie_origem_proced,'S','S')))
		and	((coalesce(b.nr_seq_forma_org,0) = 0) or (b.nr_seq_forma_org = sus_obter_estrut_proc(a.cd_procedimento,a.ie_origem_proced,'S','F')))
		and	((coalesce(b.cd_municipio_ibge,'X') = 'X') or (b.cd_municipio_ibge = sus_obter_municipio_atend(a.nr_atendimento,'C')))
		and	b.cd_estabelecimento				= a.cd_estabelecimento
		and	b.nr_sequencia					= nr_seq_regra_w;

		select	sum(coalesce(sus_obter_valor_unif(a.nr_interno_conta,0),0))
		into STRICT	vl_apac_competencia_w
		from	sus_apac_unif a,
			sus_limite_apac_comp b
		where	trunc(a.dt_emissao,'month') 				= trunc(b.dt_competencia,'month')
		and	coalesce(b.ie_tipo_financiamento,ie_tipo_financiamento_w) 	= ie_tipo_financiamento_w
		and	coalesce(b.ie_complexidade,ie_complexidade_w)		= ie_complexidade_w
		and	((coalesce(b.nr_seq_grupo,0) = 0) or (b.nr_seq_grupo = sus_obter_estrut_proc(a.cd_procedimento,a.ie_origem_proced,'S','G')))
		and	((coalesce(b.nr_seq_subgrupo,0) = 0) or (b.nr_seq_subgrupo = sus_obter_estrut_proc(a.cd_procedimento,a.ie_origem_proced,'S','S')))
		and	((coalesce(b.nr_seq_forma_org,0) = 0) or (b.nr_seq_forma_org = sus_obter_estrut_proc(a.cd_procedimento,a.ie_origem_proced,'S','F')))
		and	((coalesce(b.cd_municipio_ibge,'X') = 'X') or (b.cd_municipio_ibge = sus_obter_municipio_atend(a.nr_atendimento,'C')))
		and	b.cd_estabelecimento				= a.cd_estabelecimento
		and	b.nr_sequencia					= nr_seq_regra_w;

		select	coalesce(sus_obter_valor_proc_unif(clock_timestamp(),cd_procedimento_p,7,cd_estabelecimento_p,1),0)
		into STRICT	vl_apac_atual_w
		;

		qt_apac_competencia_w	:= qt_apac_competencia_w + 1;
		vl_apac_competencia_w	:= vl_apac_competencia_w + vl_apac_atual_w;

		end;
	else
		begin
		select	count(*)
		into STRICT	qt_apac_competencia_w
		from	sus_apac_unif a,
			sus_limite_apac_comp b,
			conta_paciente c
		where	trunc(c.dt_mesano_referencia,'month') 			= trunc(b.dt_competencia,'month')
		and	coalesce(b.ie_tipo_financiamento,ie_tipo_financiamento_w) 	= ie_tipo_financiamento_w
		and	coalesce(b.ie_complexidade,ie_complexidade_w)		= ie_complexidade_w
		and	((coalesce(b.nr_seq_grupo,0) = 0) or (b.nr_seq_grupo = sus_obter_estrut_proc(a.cd_procedimento,a.ie_origem_proced,'S','G')))
		and	((coalesce(b.nr_seq_subgrupo,0) = 0) or (b.nr_seq_subgrupo = sus_obter_estrut_proc(a.cd_procedimento,a.ie_origem_proced,'S','S')))
		and	((coalesce(b.nr_seq_forma_org,0) = 0) or (b.nr_seq_forma_org = sus_obter_estrut_proc(a.cd_procedimento,a.ie_origem_proced,'S','F')))
		and	((coalesce(b.cd_municipio_ibge,'X') = 'X') or (b.cd_municipio_ibge = sus_obter_municipio_atend(a.nr_atendimento,'C')))
		and	b.cd_estabelecimento				= a.cd_estabelecimento
		and	a.nr_interno_conta					= c.nr_interno_conta
		and	b.nr_sequencia					= nr_seq_regra_w;

		select	sum(coalesce(sus_obter_valor_unif(a.nr_interno_conta,0),0))
		into STRICT	vl_apac_competencia_w
		from	sus_apac_unif a,
			sus_limite_apac_comp b,
			conta_paciente c
		where	trunc(c.dt_mesano_referencia,'month') 			= trunc(b.dt_competencia,'month')
		and	coalesce(b.ie_tipo_financiamento,ie_tipo_financiamento_w) 	= ie_tipo_financiamento_w
		and	coalesce(b.ie_complexidade,ie_complexidade_w)		= ie_complexidade_w
		and	((coalesce(b.nr_seq_grupo,0) = 0) or (b.nr_seq_grupo = sus_obter_estrut_proc(a.cd_procedimento,a.ie_origem_proced,'S','G')))
		and	((coalesce(b.nr_seq_subgrupo,0) = 0) or (b.nr_seq_subgrupo = sus_obter_estrut_proc(a.cd_procedimento,a.ie_origem_proced,'S','S')))
		and	((coalesce(b.nr_seq_forma_org,0) = 0) or (b.nr_seq_forma_org = sus_obter_estrut_proc(a.cd_procedimento,a.ie_origem_proced,'S','F')))
		and	((coalesce(b.cd_municipio_ibge,'X') = 'X') or (b.cd_municipio_ibge = sus_obter_municipio_atend(a.nr_atendimento,'C')))
		and	b.cd_estabelecimento				= a.cd_estabelecimento
		and	a.nr_interno_conta					= c.nr_interno_conta
		and	b.nr_sequencia					= nr_seq_regra_w;


		select	coalesce(sus_obter_valor_unif(nr_interno_conta_p,0),0)
		into STRICT	vl_apac_atual_w
		;

		qt_apac_competencia_w	:= qt_apac_competencia_w + 1;
		vl_apac_competencia_w	:= vl_apac_competencia_w + vl_apac_atual_w;

		end;
	end if;

	if (nr_seq_regra_w <> 0) then
		begin
		if (qt_apac_competencia_w > qt_apac_regra_w) then
			--ds_retorno_w := 'A quantidade ('||qt_apac_competencia_w||') ultrapassou o limite que é de ('||qt_apac_regra_w||') apac(s) para a competência '||to_char(dt_competencia_p,'mm/yyyy')||'.';
			ds_retorno_w := wheb_mensagem_pck.get_texto(299673, 'QT_AIH_COMPETENCIA=' || qt_apac_competencia_w || ';QT_AIH_REGRA=' || qt_apac_regra_w || ';DT_COMPETENCIA=' || to_char(dt_competencia_p,'mm/yyyy'));
		end if;
		if (vl_apac_competencia_w > vl_apac_regra_w) then
			--ds_retorno_w := 'O valor (R$:'||vl_apac_competencia_w||') ultrapassou o limite que é de (R$:'||vl_apac_regra_w||') para as apac(s) da competência '||to_char(dt_competencia_p,'mm/yyyy')||'.';
			ds_retorno_w := wheb_mensagem_pck.get_texto(299674, 'VL_AIH_COMPETENCIA=' || vl_apac_competencia_w || ';VL_AIH_REGRA=' || vl_apac_regra_w || ';DT_COMPETENCIA=' || to_char(dt_competencia_p,'mm/yyyy'));
		end if;
		end;
	end if;
	end;
end if;

return	ds_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
-- REVOKE ALL ON FUNCTION sus_consiste_limite_apac ( dt_competencia_p timestamp, cd_procedimento_p bigint, cd_estabelecimento_p bigint, nr_interno_conta_p bigint, ie_opcao_p text) FROM PUBLIC;

