CREATE OR REPLACE FUNCTION sus_consiste_nraplic_cid ( cd_doenca_cid_p text, qt_campos_radio_p bigint) RETURNS varchar AS $body$
DECLARE


ds_retorno_w		varchar(1) := 'N';
qt_campos_radio_w		smallint;


BEGIN
if (coalesce(cd_doenca_cid_p,'X') <> 'X') and (coalesce(qt_campos_radio_p,0) > 0) then
	begin

	select	coalesce(max(somente_numero(qt_campos_radio)),0)
	into STRICT	qt_campos_radio_w
	from	cid_doenca
	where	cd_doenca_cid	= cd_doenca_cid_p;

	if (qt_campos_radio_p > qt_campos_radio_w) then
		begin
		ds_retorno_w	:= 'S';
		end;
	end if;
	end;
end if;

return	ds_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
