CREATE OR REPLACE FUNCTION sus_consiste_proced_atend (nr_atendimento_p bigint, cd_procedimento_p bigint, ie_origem_proced_p text) RETURNS varchar AS $body$
DECLARE

ds_tipo_atend_w			varchar(4)	:= '';
ie_tipo_atendimento_w		smallint;
qt_reg_apac_w			bigint;
ds_retorno_w			varchar(10);

BEGIN

select	max(ie_tipo_atendimento)
into STRICT	ie_tipo_atendimento_w
from	atendimento_paciente
where	nr_Atendimento	= nr_atendimento_p;
if (ie_tipo_atendimento_w	= 1) then
	ds_tipo_atend_w	:= 'AIH';
else
	select	count(*)
	into STRICT	qt_reg_apac_w
	from	sus_apac_unif
	where	nr_atendimento	= nr_atendimento_p;
	if (qt_reg_apac_w	> 0) then
		ds_tipo_atend_w	:= 'APAC';
	else
		ds_tipo_atend_w	:= 'BPA';
	end if;

end if;

select	coalesce(max('S'),'N')
into STRICT	ds_retorno_w
from	sus_procedimento_registro
where	cd_procedimento		= cd_procedimento_p
and	ie_origem_proced	= ie_origem_proced_p
and	(((ds_tipo_atend_w	= 'BPA') and (cd_registro in (1,2,6,7))) or
	((ds_tipo_atend_w	= 'AIH') and (cd_registro in (3,4,5))) or
	((ds_tipo_atend_w	= 'APAC') and (cd_registro in (1,2,6,7))));

return	ds_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
