CREATE OR REPLACE FUNCTION sus_converte_idade_anos ( vl_idade_p bigint, ie_opcao_p text) RETURNS varchar AS $body$
DECLARE

/*
D - Converte a idade e retorno a idade mais meses ou anos
C - Converte a idade e retorna somente a idade
*/
ds_retorno_w	varchar(15);


BEGIN

if (vl_idade_p IS NOT NULL AND vl_idade_p::text <> '') then
	begin
	if (vl_idade_p < 12) then
		begin
		if (ie_opcao_p = 'D') then
			begin
			ds_retorno_w	:= vl_idade_p ||' '|| wheb_mensagem_pck.get_texto(311230);--' meses';
			end;
		else
			begin
			ds_retorno_w	:= trunc((vl_idade_p/12),2);
			end;
		end if;
		end;
	else
		begin
		if (ie_opcao_p = 'D') then
			begin
			ds_retorno_w	:= trunc(vl_idade_p/12) ||' '|| wheb_mensagem_pck.get_texto(279033);--' anos';
			end;
		else
			begin
			ds_retorno_w	:= trunc(vl_idade_p/12);
			end;
		end if;
		end;
	end if;
	end;
else
	ds_retorno_w	:= '';
end if;

return	ds_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
