CREATE OR REPLACE FUNCTION sus_gerar_uuid_esus ( cd_cnes_p estabelecimento.cd_cns%type, ie_opcao_p text) RETURNS varchar AS $body$
DECLARE

cd_uuid_w	bytea;
ds_valor_w	varchar(39);
ds_retorno_w	varchar(45);

/*
Formato pad√£o:
5444428-49fe27e4-e779-4c94-86e2-ec0f390ab3c8
*/
BEGIN

cd_uuid_w := dbms_crypto.randombytes(16);

ds_valor_w := lower(utl_raw.overlay(utl_raw.bit_or(utl_raw.bit_and(utl_raw.substr(cd_uuid_w, 7, 1), '0F'), '40'), cd_uuid_w, 7));

ds_valor_w	:=	lpad(cd_cnes_p||ds_valor_w,39,0);

if (ie_opcao_p = 'C') then
	begin
	ds_retorno_w	:= ds_valor_w;
	end;
else
	begin
	ds_retorno_w	:= 	substr(substr(ds_valor_w,1,7) || '-' ||
				substr(ds_valor_w,8,8) || '-' ||
				substr(ds_valor_w,16,4) || '-' ||
				substr(ds_valor_w,20,4) || '-' ||
				substr(ds_valor_w,24,4) || '-' ||
				substr(ds_valor_w,28,39),1,44);
	end;
end if;

return	ds_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
