CREATE OR REPLACE FUNCTION sus_mostrar_contas_lote ( nr_seq_lote_p bigint) RETURNS varchar AS $body$
DECLARE

					 
ds_retorno_w		varchar(255) := 'N';
qt_contas_sem_protocolo_w	bigint := 0;					
 

BEGIN 
 
begin 
select	count(*) 
into STRICT	qt_contas_sem_protocolo_w 
from	sus_laudo_paciente a, 
	conta_paciente b 
where	a.nr_interno_conta = b.nr_interno_conta 
and	coalesce(b.nr_seq_protocolo,0) = 0 
and	a.nr_seq_lote = nr_seq_lote_p;
exception 
when others then 
	ds_retorno_w := 'N';
end;
 
if (qt_contas_sem_protocolo_w > 0) then 
	ds_retorno_w := 'S';
end if;
 
return	coalesce(ds_retorno_w,'N');
 
end;
