CREATE OR REPLACE FUNCTION sus_obter_aihunif_conta_seq ( nr_interno_conta_p bigint) RETURNS varchar AS $body$
DECLARE



nr_aih_w		bigint;
nr_sequencia_w		bigint;
ds_retorno_w		varchar(20);


BEGIN

select	coalesce(max(nr_aih),null),
	coalesce(max(nr_sequencia),null)
into STRICT	nr_aih_w,
	nr_sequencia_w
from	sus_aih_unif
where	nr_interno_conta	= nr_interno_conta_p;

if (nr_aih_w IS NOT NULL AND nr_aih_w::text <> '') and (nr_sequencia_w IS NOT NULL AND nr_sequencia_w::text <> '') then
	begin
	ds_retorno_w	:= nr_aih_w || ' - ' || nr_sequencia_w;
	end;
end if;

return	ds_retorno_w;

END;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
