CREATE OR REPLACE FUNCTION sus_obter_apacunif_conta ( nr_interno_conta_p bigint) RETURNS varchar AS $body$
DECLARE


ds_retorno_w	varchar(255);
nr_apac_w	bigint;
nr_sequencia_w	bigint;


BEGIN

begin
select	max(nr_apac),
	max(nr_sequencia)
into STRICT	nr_apac_w,
	nr_sequencia_w
from	sus_apac_unif
where	nr_interno_conta	= nr_interno_conta_p;
exception
when others then
	ds_retorno_w		:= '';
end;

if (nr_apac_w IS NOT NULL AND nr_apac_w::text <> '') and (nr_sequencia_w IS NOT NULL AND nr_sequencia_w::text <> '') then
	begin
	ds_retorno_w	:= nr_apac_w || ' - ' || nr_sequencia_w;
	end;
end if;
return ds_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
