CREATE OR REPLACE FUNCTION sus_obter_apac_atend_vig ( nr_atendimento_p bigint, dt_parametro_p timestamp, ie_opcao_p text) RETURNS varchar AS $body$
DECLARE

 
/*			 
NA - Número do APAC 
FV - Fim da validade do APAC 
PF - Número da APAC por Pessoa física 
*/
			 
nr_seq_interno_w		bigint;
ds_retorno_w		varchar(20);			
cd_pessoa_fisica_w	varchar(10);


BEGIN 
 
if (ie_opcao_p = 'PF') then 
	select	max(cd_pessoa_fisica) 
	into STRICT	cd_pessoa_fisica_w 
	from	atendimento_paciente 
	where	nr_atendimento = nr_atendimento_p;
 
	select	max(x.nr_seq_interno) 
	into STRICT	nr_seq_interno_w 
	from	sus_laudo_paciente x 
	where	x.nr_atendimento in (SELECT y.nr_atendimento from atendimento_paciente y where y.cd_pessoa_fisica = cd_pessoa_fisica_w) 
	and	dt_parametro_p between x.dt_inicio_val_apac and x.dt_fim_val_apac;
	 
	select	max(to_char(nr_apac)) 
	into STRICT	ds_retorno_w 
	from	sus_laudo_paciente 
	where	nr_seq_interno = nr_seq_interno_w;
else 
	select	max(x.nr_seq_interno) 
	into STRICT	nr_seq_interno_w 
	from	sus_laudo_paciente x 
	where	x.nr_atendimento = nr_atendimento_p 
	and	dt_parametro_p between x.dt_inicio_val_apac and x.dt_fim_val_apac;
end if;
	 
if (ie_opcao_p = 'NA') then 
	begin 
	select	max(to_char(nr_apac)) 
	into STRICT	ds_retorno_w 
	from	sus_laudo_paciente 
	where	nr_seq_interno = nr_seq_interno_w;
	end;
end if;
 
if (ie_opcao_p = 'FV') then 
	begin 
	select	max(to_char(dt_fim_val_apac,'dd/mm/yyyy')) 
	into STRICT	ds_retorno_w 
	from	sus_laudo_paciente 
	where	nr_seq_interno = nr_seq_interno_w;
	end;
end if;
 
if (ie_opcao_p = 'AP') then 
	select 	coalesce(max(IE_CONTINUIDADE_TRAT),'N') 
	into STRICT	ds_retorno_w 
	from	sus_laudo_paciente 
	where	nr_seq_interno = nr_seq_interno_w;
	 
end if;
	 
return	ds_retorno_w;
 
end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
