SET client_encoding TO 'UTF8';





CREATE OR REPLACE FUNCTION sus_obter_autorizador_aih ( nr_interno_conta_p bigint, ie_opcao_p text) RETURNS varchar AS $body$
DECLARE


/* ie_opcao_p
	- CPF
	- PF
*/
cd_pessoa_fisica_w		varchar(10);
cd_estabelecimento_w		bigint;
cd_medico_autorizador_w		varchar(10);
nr_cpf_w			varchar(11);
ds_retorno_w			varchar(200);



BEGIN

select	b.cd_pessoa_fisica,
	a.cd_estabelecimento
into STRICT	cd_pessoa_fisica_w,
	cd_estabelecimento_w
from	atendimento_paciente	b,
	conta_paciente		a
where	a.nr_atendimento	= b.nr_atendimento
and	a.nr_interno_conta	= nr_interno_conta_p;

select	max(cd_medico_autorizador)
into STRICT	cd_medico_autorizador_w
from	sus_aih_unif
where	nr_interno_conta	= nr_interno_conta_p;

if (coalesce(cd_medico_autorizador_w::text, '') = '') then
	select	coalesce(max(b.cd_medico_autorizador),'')
	into STRICT	cd_medico_autorizador_w
	from	sus_municipio		b,
		compl_pessoa_fisica	a
	where	a.cd_pessoa_fisica	= cd_pessoa_fisica_w
	and	a.ie_tipo_complemento	= 1
	and	a.cd_municipio_ibge	= b.cd_municipio_ibge;
end if;

if (coalesce(cd_medico_autorizador_w::text, '') = '') then
	select	cd_medico_autorizador
	into STRICT	cd_medico_autorizador_w
	from	sus_parametros_aih
	where	cd_estabelecimento	= cd_estabelecimento_w;
end if;

if (ie_opcao_p	= 'CPF') then
	select	coalesce(max(nr_cpf),'')
	into STRICT	nr_cpf_w
	from	pessoa_fisica
	where	cd_pessoa_fisica	= cd_medico_autorizador_w;
	ds_retorno_w	:= nr_cpf_w;
elsif (ie_opcao_p	= 'PF') then
	ds_retorno_w	:= cd_medico_autorizador_w;
end if;

return ds_retorno_w;

END;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
-- REVOKE ALL ON FUNCTION sus_obter_autorizador_aih ( nr_interno_conta_p bigint, ie_opcao_p text) FROM PUBLIC;

