SET client_encoding TO 'UTF8';





CREATE OR REPLACE FUNCTION sus_obter_bpa_regra_cnes ( nr_seq_regra_p text, ie_opcao_p text, ie_tipo_p text) RETURNS bigint AS $body$
DECLARE

/*Opção:
P - Protocolo
E - Execução(fora de protocolo)
Tipo retorno
V - Valor
Q - Quantidade
*/
qt_retorno_w		bigint;
vl_retorno_w		double precision;
ds_retorno_w		double precision;
cd_estabelecimento_w	smallint;
ie_complexidade_w		varchar(15);
ie_tipo_financiamento_w	varchar(15);
dt_competencia_w		timestamp;
nr_seq_grupo_w		bigint := 0;
nr_seq_subgrupo_w		bigint := 0;
nr_seq_forma_org_w	bigint := 0;
cd_municipio_ibge_w	varchar(6) := 'X';


BEGIN

begin
select	cd_estabelecimento,
	dt_competencia,
	ie_complexidade,
	ie_tipo_financiamento,
	nr_seq_grupo,
	nr_seq_subgrupo,
	nr_seq_forma_org,
	cd_municipio_ibge
into STRICT	cd_estabelecimento_w,
	dt_competencia_w,
	ie_complexidade_w,
	ie_tipo_financiamento_w,
	nr_seq_grupo_w,
	nr_seq_subgrupo_w,
	nr_seq_forma_org_w,
	cd_municipio_ibge_w
from	sus_limite_bpa_comp
where	nr_sequencia = nr_seq_regra_p;
exception
when others then
	cd_estabelecimento_w	:= 0;
	dt_competencia_w		:= clock_timestamp();
	ie_complexidade_w		:= null;
	ie_tipo_financiamento_w	:= null;
	nr_seq_grupo_w		:= 0;
	nr_seq_subgrupo_w	:= 0;
	nr_seq_forma_org_w	:= 0;
	cd_municipio_ibge_w	:= 'X';
end;

if (ie_opcao_p = 'P') then
	begin
	if (ie_tipo_p = 'Q') then
		begin

		select	count(*)
		into STRICT	qt_retorno_w
		from	sus_bpa_unif a,
			conta_paciente b,
			protocolo_convenio c,
			sus_grupo d,
			sus_subgrupo e,
			sus_forma_organizacao f,
			sus_procedimento g,
			procedimento_paciente h
		where	a.nr_interno_conta 			= b.nr_interno_conta
		and	h.nr_interno_conta 			= b.nr_interno_conta
		and	h.cd_procedimento			= g.cd_procedimento
		and	h.ie_origem_proced			= g.ie_origem_proced
		and	g.nr_seq_forma_org			= f.nr_sequencia
		and	f.nr_seq_subgrupo			= e.nr_sequencia
		and	e.nr_seq_grupo				= d.nr_sequencia
		and	coalesce(h.cd_motivo_exc_conta::text, '') = ''
		and	trunc(b.dt_mesano_referencia,'month')	= trunc(dt_competencia_w,'month')
		and 	g.ie_complexidade	= coalesce(ie_complexidade_w,g.ie_complexidade)
		and	g.ie_tipo_financiamento 	= coalesce(ie_tipo_financiamento_w,g.ie_tipo_financiamento)
		and (d.nr_sequencia = coalesce(nr_seq_grupo_w,d.nr_sequencia))
		and (e.nr_sequencia = coalesce(nr_seq_subgrupo_w,e.nr_sequencia))
		and (f.nr_sequencia = coalesce(nr_seq_forma_org_w,f.nr_sequencia))
		and	((coalesce(cd_municipio_ibge_w,'X') = 'X') or (sus_obter_municipio_atend(a.nr_atendimento,'C') = cd_municipio_ibge_w))
		and	b.cd_estabelecimento			= cd_estabelecimento_w
		and	b.nr_seq_protocolo			= c.nr_seq_protocolo;

		ds_retorno_w := qt_retorno_w;

		end;
	elsif (ie_tipo_p = 'V') then
		begin

		select	sum(coalesce(c.vl_procedimento,0))
		into STRICT	vl_retorno_w
		from	sus_bpa_unif a,
			conta_paciente b,
			procedimento_paciente c,
			protocolo_convenio d,
			sus_grupo e,
			sus_subgrupo f,
			sus_forma_organizacao g,
			sus_procedimento h
		where	a.nr_interno_conta 			= b.nr_interno_conta
		and	c.nr_interno_conta 			= b.nr_interno_conta
		and	c.cd_procedimento			= h.cd_procedimento
		and	c.ie_origem_proced			= h.ie_origem_proced
		and	h.nr_seq_forma_org			= g.nr_sequencia
		and	g.nr_seq_subgrupo			= f.nr_sequencia
		and	f.nr_seq_grupo				= e.nr_sequencia
		and	coalesce(c.cd_motivo_exc_conta::text, '') = ''
		and	trunc(b.dt_mesano_referencia,'month')	= trunc(dt_competencia_w,'month')
		and 	h.ie_complexidade	= coalesce(ie_complexidade_w,h.ie_complexidade)
		and	h.ie_tipo_financiamento 	= coalesce(ie_tipo_financiamento_w,h.ie_tipo_financiamento)
		and (e.nr_sequencia = coalesce(nr_seq_grupo_w,e.nr_sequencia))
		and (f.nr_sequencia = coalesce(nr_seq_subgrupo_w,f.nr_sequencia))
		and (g.nr_sequencia = coalesce(nr_seq_forma_org_w,g.nr_sequencia))
		and	((coalesce(cd_municipio_ibge_w,'X') = 'X') or (sus_obter_municipio_atend(a.nr_atendimento,'C') = cd_municipio_ibge_w))
		and	b.cd_estabelecimento			= cd_estabelecimento_w
		and	c.ie_doc_executor			= 5
		and	b.nr_seq_protocolo			= d.nr_seq_protocolo;

		ds_retorno_w := vl_retorno_w;

		end;
	end if;
	end;
elsif (ie_opcao_p = 'E') then
	begin

	if (ie_tipo_p = 'Q') then
		begin

		select	count(*)
		into STRICT	qt_retorno_w
		from	sus_bpa_unif a,
			conta_paciente b,
			protocolo_convenio c,
			sus_grupo d,
			sus_subgrupo e,
			sus_forma_organizacao f,
			sus_procedimento g,
			procedimento_paciente h
		where	a.nr_interno_conta 			= b.nr_interno_conta
		and	h.nr_interno_conta 			= b.nr_interno_conta
		and	h.cd_procedimento			= g.cd_procedimento
		and	h.ie_origem_proced			= g.ie_origem_proced
		and	g.nr_seq_forma_org			= f.nr_sequencia
		and	f.nr_seq_subgrupo			= e.nr_sequencia
		and	e.nr_seq_grupo				= d.nr_sequencia
		and	trunc(b.dt_mesano_referencia,'month')	= trunc(dt_competencia_w,'month')
		and 	g.ie_complexidade	= coalesce(ie_complexidade_w,g.ie_complexidade)
		and	g.ie_tipo_financiamento 	= coalesce(ie_tipo_financiamento_w,g.ie_tipo_financiamento)
		and (d.nr_sequencia = coalesce(nr_seq_grupo_w,d.nr_sequencia))
		and (e.nr_sequencia = coalesce(nr_seq_subgrupo_w,e.nr_sequencia))
		and (f.nr_sequencia = coalesce(nr_seq_forma_org_w,f.nr_sequencia))
		and	((coalesce(cd_municipio_ibge_w,'X') = 'X') or (sus_obter_municipio_atend(a.nr_atendimento,'C') = cd_municipio_ibge_w))
		and	b.cd_estabelecimento			= cd_estabelecimento_w
		and	coalesce(b.nr_seq_protocolo,0) 		= 0;

		ds_retorno_w := qt_retorno_w;

		end;
	elsif (ie_tipo_p = 'V') then
		begin

		select	sum(coalesce(c.vl_procedimento,0))
		into STRICT	vl_retorno_w
		from	sus_bpa_unif a,
			conta_paciente b,
			procedimento_paciente c,
			sus_grupo d,
			sus_subgrupo e,
			sus_forma_organizacao f,
			sus_procedimento g
		where	1 = 1
		and 	a.nr_interno_conta 			= b.nr_interno_conta
		and	c.nr_interno_conta 			= b.nr_interno_conta
		and	c.cd_procedimento			= g.cd_procedimento
		and	c.ie_origem_proced			= g.ie_origem_proced
		and	g.nr_seq_forma_org			= f.nr_sequencia
		and	f.nr_seq_subgrupo			= e.nr_sequencia
		and	e.nr_seq_grupo				= d.nr_sequencia
		and	trunc(b.dt_mesano_referencia,'month')	= trunc(dt_competencia_w,'month')
		and 	g.ie_complexidade	= coalesce(ie_complexidade_w,g.ie_complexidade)
		and	g.ie_tipo_financiamento 	= coalesce(ie_tipo_financiamento_w,g.ie_tipo_financiamento)
		and (d.nr_sequencia = coalesce(nr_seq_grupo_w,d.nr_sequencia))
		and (e.nr_sequencia = coalesce(nr_seq_subgrupo_w,e.nr_sequencia))
		and (f.nr_sequencia = coalesce(nr_seq_forma_org_w,f.nr_sequencia))
		and	((coalesce(cd_municipio_ibge_w,'X') = 'X') or (sus_obter_municipio_atend(a.nr_atendimento,'C') = cd_municipio_ibge_w))
		and	b.cd_estabelecimento			= cd_estabelecimento_w
		and	c.ie_doc_executor			= 5
		and	coalesce(b.nr_seq_protocolo,0) 		= 0;

		ds_retorno_w := vl_retorno_w;

		end;
	end if;

	end;
end if;

return	coalesce(ds_retorno_w,0);

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
