CREATE OR REPLACE FUNCTION sus_obter_cid_aih ( nr_atendimento_p bigint, nr_interno_conta_p bigint, cd_procedimento_p bigint, ie_tipo_cid_p text) RETURNS varchar AS $body$
DECLARE


cd_cid_w	varchar(10);


BEGIN

select	coalesce(max(CASE WHEN ie_tipo_cid_p='P' THEN cd_cid_principal WHEN ie_tipo_cid_p='S' THEN cd_cid_secundario WHEN ie_tipo_cid_p='C' THEN cd_cid_causas_assoc WHEN ie_tipo_cid_p='M' THEN cd_cid_causa_morte END ),'X')
into STRICT	cd_cid_w
from	sus_aih
where	nr_interno_conta = nr_interno_conta_p;

if (cd_cid_w = 'X') then
	begin
	select	max(cd_doenca)
	into STRICT	cd_cid_w
	from diagnostico_doenca
	where nr_atendimento =  nr_atendimento_p
	  and ie_classificacao_doenca = ie_tipo_cid_p;
	exception
		when no_data_found then
			select	CASE WHEN ie_tipo_cid_p='P' THEN cd_doenca_cid  ELSE cd_cid_secundario END
			into STRICT	cd_cid_w
			from procedimento
			where ie_origem_proced = 2
			  and cd_procedimento = cd_procedimento_p;
	end;
end if;

return cd_cid_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
