CREATE OR REPLACE FUNCTION sus_obter_cnes_estab_unif ( cd_estabelecimento_p bigint, ie_opcao_p text) RETURNS bigint AS $body$
DECLARE


cd_cnes_w	integer;
/*
A - AIH
B - BPA
P - APAC
*/
BEGIN
if (ie_opcao_p = 'B') then
	begin
	select	max(cd_cnes_hospital)
	into STRICT	cd_cnes_w
	from	sus_parametros_bpa
	where	cd_estabelecimento = cd_estabelecimento_p;
	end;
elsif (ie_opcao_p = 'P') then
	begin
	select	max(cd_cnes_hospital)
	into STRICT	cd_cnes_w
	from	sus_parametros_apac
	where	cd_estabelecimento = cd_estabelecimento_p;

	end;
else
	begin
	select	max(cd_cnes_hospital)
	into STRICT	cd_cnes_w
	from	sus_parametros_aih
	where	cd_estabelecimento = cd_estabelecimento_p;
	end;
end if;

RETURN cd_cnes_w;

END;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
