CREATE OR REPLACE FUNCTION sus_obter_complexidade_proced ( cd_procedimento_p bigint, ie_origem_proced_p bigint, ie_opcao_p text) RETURNS varchar AS $body$
DECLARE


ie_complexidade_w		varchar(2);
ds_retorno_w			varchar(120);


BEGIN

if (cd_procedimento_p > 0) and (ie_origem_proced_p > 0) then

	begin
	select	ie_complexidade
	into STRICT	ie_complexidade_w
	from	sus_procedimento
	where	cd_procedimento		= cd_procedimento_p
	and	ie_origem_proced	= ie_origem_proced_p  LIMIT 1;
	exception
	when others then
		ie_complexidade_w := '0';
	end;

	if (ie_opcao_p	= 'C') then
		ds_retorno_w		:= ie_complexidade_w;
	elsif (ie_opcao_p	= 'D') then
		ds_retorno_w		:= substr(obter_valor_dominio(1763,ie_complexidade_w),1,120);
	end if;
end if;

return	ds_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
