CREATE OR REPLACE FUNCTION sus_obter_cor_pele ( cd_pessoa_fisica_p text, ie_tipo_retorno_p text) RETURNS varchar AS $body$
DECLARE



cd_raca_cor_sus_w	smallint	:= 99;
ds_cor_pele_w		varchar(40);
ds_retorno_w		varchar(40);



BEGIN

begin
select	cd_raca_cor_sus,
	ds_cor_pele
into STRICT	cd_raca_cor_sus_w,
	ds_cor_pele_w
from	cor_pele	b,
	pessoa_fisica	a
where	a.nr_seq_cor_pele	= b.nr_sequencia
and	a.cd_pessoa_fisica	= cd_pessoa_fisica_p;
exception
	when others then
		cd_raca_cor_sus_w	:= 99;
end;

if (ie_tipo_retorno_p	= 'C') then
	ds_retorno_w	:=	cd_raca_cor_sus_w;
elsif (ie_tipo_retorno_p	= 'D') then
	ds_retorno_w	:=	ds_cor_pele_w;
end if;

return	ds_retorno_w;

END;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
