CREATE OR REPLACE FUNCTION sus_obter_dados_apac ( nr_apac_p bigint, ie_opcao_p text) RETURNS varchar AS $body$
DECLARE


ds_retorno_w		varchar(255) := '';
dt_ocorrencia_w		timestamp;

/*IE_OPCAO_P:
OC - Data de ocorrÃªncia*/
BEGIN

select	max(dt_ocorrencia)
into STRICT	dt_ocorrencia_w
from	sus_apac_unif
where 	nr_apac		= nr_apac_p;

if (ie_opcao_p = 'OC') then
	ds_retorno_w	:= to_char(dt_ocorrencia_w,'dd/mm/yyyy');
end if;

return	ds_retorno_w;

END;
$body$
LANGUAGE PLPGSQL
