CREATE OR REPLACE FUNCTION sus_obter_dados_laudo_ach ( nr_interno_conta_p bigint, ie_tipo_laudo_p bigint, ie_opcao_p text) RETURNS varchar AS $body$
DECLARE

 
ds_retorno_w			varchar(255) := '';
dt_emissao_w			timestamp;
nr_ordem_laudo_w			bigint;
nr_ordem_laudo_ww			varchar(6);
dt_retorno_secr_w			timestamp;
cd_prestador_laudo_ach_w		varchar(20);
cd_procedimento_solic_w		bigint;


BEGIN 
 
if (ie_opcao_p = 'DTE') then 
	begin 
	 
	select	max(dt_emissao) 
	into STRICT	dt_emissao_w 
	from	sus_laudo_paciente 
	where 	nr_interno_conta = nr_interno_conta_p 
	and	ie_tipo_laudo_sus = ie_tipo_laudo_p;
	 
	ds_retorno_w	:= to_char(dt_emissao_w,'ddmmyyyy');
	end;
elsif (ie_opcao_p = 'DTR') then 
	begin 
	 
	select	max(dt_retorno_secr) 
	into STRICT	dt_retorno_secr_w 
	from	sus_laudo_paciente 
	where 	nr_interno_conta = nr_interno_conta_p 
	and	ie_tipo_laudo_sus = ie_tipo_laudo_p;
	 
	ds_retorno_w	:= to_char(dt_retorno_secr_w,'dd/mm/yyyy');
	end;
elsif (ie_opcao_p = 'NRL') then 
	begin 
	 
	select	max(dt_emissao), 
		coalesce(max(nr_ordem_laudo),0) 
	into STRICT	dt_emissao_w, 
		nr_ordem_laudo_w 
	from	sus_laudo_paciente 
	where 	nr_interno_conta = nr_interno_conta_p 
	and	ie_tipo_laudo_sus = ie_tipo_laudo_p;
 
	if (nr_ordem_laudo_w > 0) then 
		begin 
		 
		nr_ordem_laudo_ww := substr(adiciona_zeros_esquerda(substr(to_char(nr_ordem_laudo_w),1,6),6),1,6);
		 
		select	max(cd_prestador_laudo_ach) 
		into STRICT	cd_prestador_laudo_ach_w 
		from	conta_paciente a, 
			sus_parametros_aih b 
		where	a.cd_estabelecimento = b.cd_estabelecimento 
		and	a.nr_interno_conta = nr_interno_conta_p;
		 
		ds_retorno_w	:= 	substr(to_char(dt_emissao_w,'yyyy') || 
					cd_prestador_laudo_ach_w || 
					nr_ordem_laudo_ww || 
					calcula_Digito('MODULO11',to_char(dt_emissao_w,'yyyy') || 
					cd_prestador_laudo_ach_w || 
					nr_ordem_laudo_ww ),1,13);
		end;
	end if;
	 
	end;
elsif (ie_opcao_p = 'PRO') then 
	begin 
	 
	select	max(cd_procedimento_solic) 
	into STRICT	cd_procedimento_solic_w 
	from	sus_laudo_paciente 
	where 	nr_interno_conta = nr_interno_conta_p 
	and	ie_tipo_laudo_sus = ie_tipo_laudo_p;
	 
	ds_retorno_w	:= cd_procedimento_solic_w;
	 
	end;
end if;
 
return	ds_retorno_w;
 
end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
