CREATE OR REPLACE FUNCTION sus_obter_dados_laudo_aih ( nr_aih_p bigint, ie_opcao_p text) RETURNS varchar AS $body$
DECLARE

 
ds_retorno_w		varchar(255);
dt_emissao_w		timestamp;
dt_retorno_secr_w		timestamp;


BEGIN 
 
select	max(dt_emissao), 
	max(dt_retorno_secr) 
into STRICT	dt_emissao_w, 
	dt_retorno_secr_w 
from	sus_laudo_paciente 
where 	nr_aih		= nr_aih_p 
and	ie_tipo_laudo_sus in (1,0);
 
if (ie_opcao_p = 'DT') then 
	ds_retorno_w	:= to_char(dt_emissao_w,'dd/mm/yyyy');
elsif (ie_opcao_p = 'DTR') then 
	ds_retorno_w	:= to_char(dt_retorno_secr_w,'dd/mm/yyyy');
 
end if;
 
return	ds_retorno_w;
 
END;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
