SET client_encoding TO 'UTF8';





CREATE OR REPLACE FUNCTION sus_obter_dados_laudo_pac ( nr_seq_interno_p bigint, ie_opcao_p text) RETURNS varchar AS $body$
DECLARE

 
/*OPções: 
MNE - Motivo da não execução 
DNE - Descrição do motivo da não execução 
*/
 
					 
ds_retorno_w			varchar(255) := '';
nr_seq_motivo_nexec_proc_w	bigint;					
 

BEGIN 
 
select	coalesce(nr_seq_motivo_nexec_proc,0) 
into STRICT	nr_seq_motivo_nexec_proc_w 
from	sus_laudo_paciente 
where	nr_seq_interno = nr_seq_interno_p;
 
if (ie_opcao_p = 'MNE') then 
	ds_retorno_w := nr_seq_motivo_nexec_proc_w;
elsif (ie_opcao_p = 'DNE') then 
	ds_retorno_w := substr(SUS_OBTER_DESC_MTV_NEXEC_LAUDO(nr_seq_motivo_nexec_proc_w),1,255);
end if;
 
return	ds_retorno_w;
 
end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
