CREATE OR REPLACE FUNCTION sus_obter_dados_proc_generico ( nr_sequencia_p bigint, cd_procedimento_p bigint, ie_opcao_p text) RETURNS varchar AS $body$
DECLARE


cd_procedimento_w	bigint	:= 0;
ds_procedimento_w		varchar(255) 	:= '';
nr_sequencia_w		bigint	:= 0;
ds_retorno_w		varchar(255)	:= '';


BEGIN

if (nr_sequencia_p IS NOT NULL AND nr_sequencia_p::text <> '') then
	begin
	begin
	select	max(cd_procedimento),
		max(substr(ds_procedimento,1,255))
	into STRICT	cd_procedimento_w,
		ds_procedimento_w
	from	sus_procedimento_generico
	where	nr_sequencia	= nr_sequencia_p;
	exception
	when others then
		cd_procedimento_w := 0;
		ds_procedimento_w := '';
	end;
	end;
else
	begin
	begin
	select	max(nr_sequencia),
		max(substr(ds_procedimento,1,255))
	into STRICT	nr_sequencia_w,
		ds_procedimento_w
	from	sus_procedimento_generico
	where	cd_procedimento	= cd_procedimento_p;
	exception
	when others then
		nr_sequencia_w 		:= 0;
		ds_procedimento_w		:= '';
	end;
	end;
end if;

if (ie_opcao_p = 'C') then
	ds_retorno_w	:= cd_procedimento_w;
elsif (ie_opcao_p = 'D') then
	ds_retorno_w	:= ds_procedimento_w;
elsif (ie_opcao_p = 'S') then
	ds_retorno_w	:= nr_sequencia_w;
end if;

return ds_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
