CREATE OR REPLACE FUNCTION sus_obter_dados_servico ( nr_sequencia_p bigint, ie_opcao_p text) RETURNS varchar AS $body$
DECLARE


cd_servico_w          smallint;
ds_servico_w          varchar(120);


BEGIN

/* ESSA FUNCTION Ã‰ UTILIZADA NA TABELA SUS_PROCEDIMENTO NO DICIONARIO DE DADOS */

select	cd_servico,
	ds_servico
into STRICT	cd_servico_w,
	ds_servico_w
from	sus_servico
where	nr_sequencia	= nr_sequencia_p  LIMIT 1;

if (ie_opcao_p = 'C') then
	return cd_servico_w;
else
	return ds_servico_w;
end if;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
