CREATE OR REPLACE FUNCTION sus_obter_dados_serv_classif ( nr_seq_serv_classif_p bigint, ie_opcao_p text) RETURNS varchar AS $body$
DECLARE


ds_retorno_w  varchar(255);
cd_servico_classif_w smallint;
ds_Servico_classif_w varchar(255);


BEGIN

select	max(cd_servico_classif),
	max(ds_servic_classif)
into STRICT 	cd_Servico_classif_w,
	ds_servico_classif_w
from 	sus_servico_classif
where 	nr_sequencia 	= nr_seq_serv_classif_p;

if (ie_opcao_p = 'C') then
	ds_retorno_w := cd_servico_classif_w;
else
	ds_retorno_w := ds_servico_classif_w;
end if;

return ds_retorno_w;

end;
