CREATE OR REPLACE FUNCTION sus_obter_dados_sismama_atend ( nr_atendimento_p bigint, ie_tipo_laudo_p text, ie_opcao_p text) RETURNS varchar AS $body$
DECLARE


/*IE_TIPO_LAUDO:
C - Citopatológico
H - Histopatológico
M - Mamografia

IE_OPCAO:
CMR - Código do médico responsável Conclusão Radiológica
NMR - Nome do médico responsável Conclusão Radiológica
*/
ds_retorno_w		varchar(255);
nr_seq_sism_w		bigint;


BEGIN

if (ie_tipo_laudo_p = 'M') then
	begin

	select	coalesce(max(nr_sequencia),0)
	into STRICT	nr_seq_sism_w
	from	sismama_atendimento
	where	(dt_liberacao IS NOT NULL AND dt_liberacao::text <> '')
	and	sus_obter_tp_laudo_sismama(nr_sequencia,1) = '1'
	and	nr_atendimento = nr_atendimento_p;

	if (ie_opcao_p = 'CMR') then
		begin

		select	coalesce(max(cd_responsavel),'')
		into STRICT	ds_retorno_w
		from	sismama_mam_conclusao
		where	nr_seq_sismama = nr_seq_sism_w;

		end;
	elsif (ie_opcao_p = 'NMR') then
		begin
		select	coalesce(max(substr(obter_nome_pf(cd_responsavel),1,100)),'')
		into STRICT	ds_retorno_w
		from	sismama_mam_conclusao
		where	nr_seq_sismama = nr_seq_sism_w;
		end;
	end if;

	end;
elsif (ie_tipo_laudo_p = 'C') then
	begin
	select	coalesce(max(nr_sequencia),0)
	into STRICT	nr_seq_sism_w
	from	sismama_atendimento
	where	(dt_liberacao IS NOT NULL AND dt_liberacao::text <> '')
	and	sus_obter_tp_laudo_sismama(nr_sequencia,1) = '2'
	and	nr_atendimento = nr_atendimento_p;
	end;
elsif (ie_tipo_laudo_p = 'H') then
	begin
	select	coalesce(max(nr_sequencia),0)
	into STRICT	nr_seq_sism_w
	from	sismama_atendimento
	where	(dt_liberacao IS NOT NULL AND dt_liberacao::text <> '')
	and	sus_obter_tp_laudo_sismama(nr_sequencia,1) = '3'
	and	nr_atendimento = nr_atendimento_p;
	end;
end if;

return	ds_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
