CREATE OR REPLACE FUNCTION sus_obter_desc_classif ( cd_parametro_p bigint, nr_seq_servico_p bigint, ie_opcao_p text) RETURNS bigint AS $body$
DECLARE



nr_retorno_w         bigint;



BEGIN
if (ie_opcao_p	= 'S') then
	select	max(nr_sequencia)
	into STRICT	nr_retorno_w
	from	sus_servico_classif
	where	cd_servico_classif	= cd_parametro_p
	and	nr_seq_servico		= nr_seq_servico_p;
elsif (ie_opcao_p	= 'C') then
	select	max(CD_SERVICO_CLASSIF)
	into STRICT	nr_retorno_w
	from	sus_servico_classif
	where	nr_sequencia	= cd_parametro_p;

end if;


return nr_retorno_w;

END;
$body$
