CREATE OR REPLACE FUNCTION sus_obter_desc_mot_apac_obito (nr_seq_mot_apac_obt_p bigint) RETURNS varchar AS $body$
DECLARE


ds_retorno_w		varchar(255);


BEGIN

begin
select	coalesce(substr(ds_motivo,1,255),'')
into STRICT	ds_retorno_w
from	sus_motiv_apac_ate_obito
where	nr_sequencia = nr_seq_mot_apac_obt_p;
exception
