CREATE OR REPLACE FUNCTION sus_obter_desc_rubrica (nr_sequencia_p sus_procedimento.nr_seq_rubrica%type) RETURNS varchar AS $body$
DECLARE



ds_retorno_w sus_rubrica.ds_rubrica%type;


BEGIN
	if (nr_sequencia_p IS NOT NULL AND nr_sequencia_p::text <> '') then

		begin
			select	ds_rubrica
			into STRICT	ds_retorno_w
			from	sus_rubrica
			where	nr_sequencia = nr_sequencia_p;
			exception
				when others then
					ds_retorno_w := '';
		end;

	else
		begin
			ds_retorno_w := '';
		end;
	end if;

return	ds_retorno_w;

end;
$body$
