CREATE OR REPLACE FUNCTION sus_obter_dias_int_aih (nr_Atendimento_p bigint) RETURNS bigint AS $body$
DECLARE

qt_dias_w	bigint;

BEGIN
 
select	coalesce(max(obter_qt_dia_internacao_sus(CD_PROCEDIMENTO_REAL, 7)),0) 
into STRICT	qt_dias_w 
from	sus_aih_unif 
where	nr_atendimento	= nr_Atendimento_p;
 
if (Obter_Funcao_Ativa	= 281) and (qt_dias_w		= 0) then 
	select	coalesce(max(obter_qt_dia_internacao_sus(CD_PROCEDIMENTO_SOLIC, 7)),0) 
	into STRICT	qt_dias_w 
	from	sus_laudo_paciente 
	where	nr_atendimento	= nr_Atendimento_p 
	and	nr_seq_interno	= (	SELECT	min(x.nr_seq_interno) 
					from	sus_laudo_paciente x 
					where	x.nr_atendimento 	= nr_Atendimento_p);
end if;
 
return	qt_dias_w;
 
end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
