SET client_encoding TO 'UTF8';





CREATE OR REPLACE FUNCTION sus_obter_dif_conta ( nr_interno_conta_p bigint, ie_opcao_p bigint) RETURNS bigint AS $body$
DECLARE


/*
ie_opcao_p :
	0 - valor total
*/
vl_sus_w	double precision;
vl_tasy_w	double precision;


BEGIN

if (ie_opcao_p = 0) then
	select	sum(coalesce(b.vl_sadt,0) + coalesce(b.vl_medico,0) + coalesce(b.vl_matmed,0))
	into STRICT	vl_sus_w
	from	sus_valor_proc_paciente b,
		procedimento_paciente a
	where	a.nr_sequencia = b.nr_sequencia
	  and	a.nr_interno_conta 	= nr_interno_conta_p
	  and	coalesce(a.cd_motivo_exc_conta::text, '') = '';

	select	sum(coalesce(CASE WHEN coalesce(b.ie_entra_conta,'N')='N' THEN a.vl_medico  ELSE 0 END ,0)) + sum(coalesce(a.vl_procedimento,0))
	into STRICT	vl_tasy_w
	FROM procedimento_paciente a
LEFT OUTER JOIN regra_honorario b ON (a.ie_responsavel_credito = b.cd_regra)
WHERE a.nr_interno_conta 	= nr_interno_conta_p and coalesce(a.cd_motivo_exc_conta::text, '') = '';

end if;

return vl_tasy_w - vl_sus_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
-- REVOKE ALL ON FUNCTION sus_obter_dif_conta ( nr_interno_conta_p bigint, ie_opcao_p bigint) FROM PUBLIC;

